<script lang="ts">
    import ExpandedPost from "../../components/post/expanded-post.svelte";

    import type {SvelteComponent, SvelteComponentTyped} from 'svelte';
    import { goto } from '$app/navigation';

    import Post from '../../components/post/PostInList.svelte'
    import { listen } from 'svelte/internal';

    import Notify from "../../components/misc/Notify.svelte"

    import {onMount, onDestroy} from 'svelte';
    import { invalidate } from "$app/navigation";;
    

    // export let posts : [typeof SvelteComponent, Record<string, any>][] = [
    //     [Post, {title: "Svelte Engineer",  description: "Does Stuff fr :)", id:0}],
    //     [Post, {title: "Wizard", description: "Must be able to carve runes at a journeyman level. Must be able to cast Fireball.", id: 1}],
    //     [Post, {title: "Farmer", description: "Must Be Good With Sheep", id: 2}],
    //     [Post, {title: "Sheep", description: "Must Be Good With Farmers", id: 3}],
    // ];

    export let data;
    const { jobs } = data;
    let posty : [typeof SvelteComponent, Record<string, any>][] = [];
    let f_posty : [typeof SvelteComponent, Record<string, any>][] = [];
    for(let i=0; i<jobs.length; i++){
        console.log(jobs[i].todo);
        //tag all the jobs
        let temp_post = [Post, 
        {title: jobs[i].todo, 
        description: jobs[i].completed, 
        id: jobs[i].id, 
        fr: (jobs[i].id % 2 == 0), 
        en: (jobs[i].id % 2 == 1), 
        sal: (i * 10) / 2, 
        bi: (jobs[i].id % 3 == 0),
        customer: (jobs[i].id % 4 == 0),
        contract: (jobs[i].id % 5 == 0),
        degree: (jobs[i].id % 6 == 0)}];
        posty.push(temp_post);
    }
       
    // export let jobs : [typeof SvelteComponent, Record<string, any>][] = data;

    function GotoProfile()
    {
        goto("/profile");
    }

    import { construct_svelte_component } from "svelte/internal";
    // post info
    export let title = "null"
    export let description = "null"
    export let poster = "null"
    export let date = "null"
    export let salary = 0; 
    export let job_id = -1
    export let visible = false; // for initial visibility

    export let search_query = "";
    export let value = 40;
    export let en = true;
    export let fr = true;
    export let bi = true;
    export let contract = true;
    export let customer = true;
    export let degree = true;
    let boot = false;

    function Clear()
    {
       search_query = "";
    }

    export let posts = f_posty;

    function Search()
    {
        f_posty = [];
        for (let i = 0; i < posty.length; i++)
        {
            if (Empty() || posty[i][1].title === "")
            {
                if (((RMatch(posty[i][1].title) && SMatch(posty[i][1].sal)) || !boot))
                {
                    //success, include it 
                    f_posty.push(posty[i]);
                }
            }
            else
            {
                if ((RMatch(posty[i][1].title) && BMatch(posty[i][1])) && SMatch(posty[i][1].sal) || !boot)
                {
                
                    //success, include it 
                    f_posty.push(posty[i]);
                }
            }
        }
        if (!boot)
        {
            boot = true;
        
        }

        //Need to catch case where none of the things are selected but it should show the whole thing
        if (f_posty.length === 0)
        {
            //f_posty = posty;
        }

        posts = f_posty;
        console.log(f_posty);
    }

    function Empty()
    {
        if (bi == false && en == false && fr == false && customer == false && contract == false && degree == false)
        {
            return true;
        }
        return false;
    }

    function SMatch(i)
    {
        //I is greater or equal to value
        return i >= value;
    }

    function RMatch(str)
    {
        let p = new RegExp(search_query);
        if (str.match(p))
        {
            return true;
        }
        return false;
    }

    //as long as one matches, then the profile is a match
    function BMatch(arr)
    {
        if (arr.en === true && arr.en == en)
        {
            return true;
        }
        if (arr.fr === true && arr.fr == fr)
        {
            return true;
        }
        if (arr.bi === true && arr.bi == bi)
        {
            return true;
        }
        if (arr.contract === true && arr.contract == contract)
        {
            return true;
        }
        if (arr.degree === true && arr.degree == degree)
        {
            return true;
        }
        if (arr.customer === true && arr.customer == customer)
        {
            return true;
        }
        return false;
    }

    function ResetSearch()
    {
        en = false;
        fr = false;
        bi = false;
        customer = false;
        contract = false;
        degree = false;
    }
    function handleMessage(event){

        title  = event.detail.title
        description = event.detail.description
        poster = event.detail.poster
        date = event.detail.date
        salary = event.detail.salary
        job_id = event.detail.j 
        visible = true;
    }


    export let your_end_point_prop;

// resfresh your_end_point_prop
    const apiInterval = setInterval(async () => {
    await invalidate("/main");
    }, 1000000); 


    onDestroy(() => {
    clearInterval(apiInterval);
});

    //not janky i swear!
    Search();
    ResetSearch();
   


    
</script>

